function longestSubstring(s) {
  let set = new Set();
  let ans = 0, i = 0, j = 0;
  while (i < s.length && j < s.length) {
    if (!set.has(s[j])) {
      set.add(s[j++]);
      ans = Math.max(ans, j - i);
    } else {
      set.delete(s[i++]);
    }
  }
  return ans;
}

